mapper.select:

select * from (select @rownum := @rownum+1 as rnum, admin_no, admin_id, admin_passwd, admin_position, admin_title, admin_name from admin, (select @rownum :=0)as rowcolum


 			 admin_name like concat('%', #{searchKeyword}, '%') 

 order by admin_no desc) as rownum_table where rnum >=#{startRow} and rnum <=#{endRow}
