mapper.select:
select * from (select @rownum := @rownum+1 as rnum, notice_no, notice_title, notice_content, notice_reg_date from notice, (select @rownum :=0)as rowcolum
where notice_title like concat('%', #{searchKeyword}, '%')
where notice_content like concat('%', #{searchKeyword}, '%') order by notice_no desc) as rownum_table where rnum >=#{startRow} and rnum <=#{endRow}

mapper.insert:
INSERT INTO board_faq (
	board_faq_type, 
	board_faq_title, 
	board_faq_content
) 
VALUES(
	#{board_faq_type}, 
	#{board_faq_title}, 
	#{board_faq_content}
)

mapper.select:
select * from (select @rownum := @rownum+1 as rnum, board_faq_no, board_faq_type, board_faq_title, board_faq_content, board_faq_reg_date from board_faq, (select @rownum :=0)as rowcolum
where board_faq_title like concat('%', #{searchKeyword}, '%')
where board_faq_content like concat('%', #{searchKeyword}, '%') order by board_faq_no desc) as rownum_table where rnum >=#{startRow} and rnum <=#{endRow}

