mapper.select:
		   select * from (select @rownum := @rownum+1 as rnum, order_no, order_type, store_code, order_date, order_pickup_date, order_pickup_time, cm_gate_passwd, order_request1, order_request2, order_price, order_use_coupon_price, order_status, order_price_method, customer_no, order_customer_name, order_store_name, order_expected_date, order_customer_phone, order_address1, order_address2, order_delivery_price from order_cm, (select @rownum :=0) as rowcolum


    			and order_date >= #{startDate} and order_date < #{endDate} 

    			and order_type like concat('%', #{searchKeyword1}, '%')

    			and order_store_name like concat('%', #{searchKeyword2}, '%')

    			and order_address1 like concat('%', #{searchKeyword3}, '%')

    order by order_date desc) as rownum_table where rnum >=#{startRow} and rnum <=#{endRow}

		   select * from (select @rownum := @rownum+1 as rnum, order_mt_no, customer_no, customer_name, product_code, order_mt_category1, order_mt_category2, order_mt_product, order_mt_count, order_mt_price, order_mt_date, order_no from order_management, (select @rownum :=0) as rowcolum


    			and order_mt_date >= #{startDate} and order_mt_date < #{endDate} 

    			and order_mt_category1 like concat('%', #{searchKeyword1}, '%')

    			and order_mt_category2 like concat('%', #{searchKeyword2}, '%')

    			and order_mt_product like concat('%', #{searchKeyword3}, '%')

    order by order_mt_date desc) as rownum_table where rnum >=#{startRow} and rnum <=#{endRow}

	  select order_mt_category1, sum(order_mt_price) as order_mt_price from order_management

    			where order_mt_date >= #{startDate} and order_mt_date < #{endDate} 
    			 group by order_mt_category1

	  select order_store_name, sum(order_price) as order_price from order_cm

    			where order_date >= #{startDate} and order_date < #{endDate} 
	            group by order_store_name

select.if:
		  select date_format(order_date, '%Y/%m/%d') as order_date, sum(order_price) as order_price from order_cm

    			where order_date >= #{startDate} and order_date < #{endDate} 
    		    group by date_format(order_date, '%Y-%m-%d') order by order_date asc
